function selectRedirect(){$(".js-select-redirect").on("change",function(){var t=$(this).val();return t&&(window.location=t),!1})}function ajaxUpdate(){$(".js-row").find("input, select, textarea").on("change",function(){var t=$(this).closest(".js-row").attr("data-post-id"),e=$(this).attr("name"),i=$(this).val();$.ajax({method:"POST",dataType:"json",data:{post_id:t,field_key:e,field_value:i}}).success(function(t){}).error(function(t,e,i){console.log(t+e+i)})})}function autoHeightTextarea(){$("textarea").each(function(){this.setAttribute("style","height:"+this.scrollHeight+"px;overflow-y:hidden;")}).on("input",function(){this.style.height="auto",this.style.height=this.scrollHeight+"px"})}function simplebartbl(){new SimpleBar($(".js-tbl-outside")[0],{autoHide:!1})}$(document).ready(function(){selectRedirect(),ajaxUpdate(),autoHeightTextarea(),simplebartbl()});